import React, { Component } from 'react';
import { Carousel, Card, Row, Col, Button } from 'antd'
import NewsShow from '../../components/NewsShow'
import './index.less'

const { Meta } = Card

const moreButton = <div className="more"><Button>更多</Button></div>;

class Home extends Component {
    state = {};

    componentWillMount() {
        const classType = ["全部课程", "科学", "地理", "文学", "历史", "艺术", "其他"];
        const classList = ["科学", "地理", "文学", "历史", "艺术", "体育", "经济", "心理学"];
        const organizationList = [
            {
                "name": "北京师范大学珠海分校",
                "description": "我是北师珠"
            },
            {
                "name": "吉林大学珠海学院",
                "description": "我是吉珠"
            },
            {
                "name": "北京理工大学珠海学院",
                "description": "我是北理珠"
            }
        ];

        const classTypeTree = this.renderClassList(classType);
        const classPhotoTree = this.renderClassPhoto(classList);
        const organizationTree = this.renderOrganization(organizationList)
        this.setState({
            classTypeTree,
            classPhotoTree,
            organizationTree
        })

    }

    // 渲染课程分类列表
    renderClassList = (data) => {
        return data.map((item, index) => {
            if(index == 0) {
                return <li style={{ fontSize: 20 }}>{item}</li>
            }
            return <li>{item}</li>
        });
    }

    // 渲染课程分类图片
    renderClassPhoto = (data) => {
        return data.map((item) => {
            return (
                <Col span="3">
                    <Card 
                        className="classes"
                        hoverable
                        style={{ width: 205, height: 150 }}
                        cover={<img src={require(`../../resource/assets/${item}.jpg`)} />} 
                    >
                        <Meta title={item} style={{ textAlign: "center", marginTop: "-18px" }}/>
                    </Card>
                </Col>
            )
        })
    }

    // 渲染机构团队图片
    renderOrganization = (data) => {
        return data.map((item) => {
            return (
                <Card
                    className="organization-list"
                    hoverable
                    style={{ width: 265, height: 256 }}
                    cover={<img src={require(`../../resource/assets/${item.name}.jpg`)}/>}
                >
                    <Meta title={item.name} description={item.description}/>
                </Card>
            )
        })
    }

    render() {
        return (
            <div className="home">
                <div className="photo">
                    <Carousel autoplay dots={false}>
                        <div><img src={require('../../resource/assets/轮播图 1.jpg')} /></div>
                        <div><img src={require('../../resource/assets/轮播图 2.jpg')} /></div>
                        <div><img src={require('../../resource/assets/轮播图 3.jpg')} /></div>
                    </Carousel> 
                </div>

                <div className="all">
                    <div className="all-classes">
                        <ul>{this.state.classTypeTree}</ul>
                    </div>
                    <div className="classes-list">
                        <Row gutter="300">
                            {this.state.classPhotoTree}
                        </Row>
                    </div>
                    {moreButton}
                </div>

                <div className="hot">
                    <h1>热门课程</h1>
                    <div className="classes-list">
                        <Row gutter="300">
                            {this.state.classPhotoTree}
                        </Row>
                    </div>
                    {moreButton}
                </div>

                <div className="organization">
                    <h1>机构团队</h1>
                    <div className="classes-list">
                        <Row gutter="300">
                            {this.state.organizationTree}
                        </Row>
                    </div>
                    {moreButton}
                </div>

                <div className="news_question">
                    <div className="news">
                        <h1>新闻资讯</h1>
                        <div className="news-card">
                            <NewsShow />
                            <NewsShow />
                            <NewsShow />
                            <NewsShow />
                        </div>
                    </div>
                    <div className="question">
                        <h1>常见问题</h1>
                    </div>
                </div>
            </div>
        );
    }
}

export default Home;
